"use strict";const mobileWidthOnly=767;var acc=document.querySelectorAll(".footer__title");acc.forEach(t=>{t.addEventListener("click",function(){if(window.innerWidth<=767){this.classList.toggle("footer__title--active");var t=this.nextElementSibling;t.style.maxHeight?t.style.maxHeight=null:t.style.maxHeight=t.scrollHeight+"px"}})});